// import { parseTime } from '@/utils'
export default {
  data() {
    return {
      tableData: [
        {
          checkbox: true,
          name: '案件处理流程',
          descript: '案件处理流程',
          updateTime: new Date()
        }
      ],
      // // 弹框标题
      dialogStatus: '',
      textMap: {
        update: '修改流程--案件处理流程',
        create: '新增流程--案件处理流程'
      },
      dialogFormVisible: false,
      temp: {
        name: '',
        remark: ''
      },
      // 修改数据索引
      updateIndex: 0
    }
  },
  methods: {
    // 新增流程
    addWork() {
      this.dialogFormVisible = true
      this.dialogStatus = 'create'
    },
    // 删除
    deleteWork() {
      this.$confirm('确认删除选中的工作流？', '提示', {
        distinguishCancelAndClose: true,
        confirmButtonText: '确认',
        cancelButtonText: '取消'
      })
        .then(() => {
          const newDate = this.tableData.filter(item => {
            if (!item.checkbox) {
              return item
            }
          })
          console.log(newDate)
          this.tableData = newDate
          this.$message({
            type: 'info',
            message: '已删除'
          })
        })
        .catch(action => {
          this.$message({
            type: 'info',
            message: '已取消删除'
          })
        })
    },
    // 修改流程
    updateWork(item, index) {
      console.log(index)
      this.updateIndex = index

      this.dialogFormVisible = true
      this.dialogStatus = 'update'
    },

    // 弹框取消
    cancel() {
      this.dialogFormVisible = false
      this.temp = {
        name: '',
        remark: ''
      }
    },
    // 弹框确定
    add() {
      const { name, remark } = this.temp
      const newData = {
        checkbox: false,
        name,
        descript: remark,
        updateTime: new Date()
      }
      if (this.dialogStatus === 'create') {
        // 新增
        this.tableData.unshift(newData)
      } else {
        // 修改
        this.tableData.splice(this.updateIndex, 1, newData)
      }
      this.dialogFormVisible = false
    }
  }
}
