export default {
  data() {
    return {
      tableData: [
        { checkbox: false, id: 201220629145151, name: '监督员上报' },
        { checkbox: false, id: 1, name: '监督员上报' },
        { checkbox: false, id: 2, name: '网上投诉' },
        { checkbox: false, id: 3, name: '市民来电' },
        { checkbox: false, id: 4, name: '巡查发现' },
        { checkbox: false, id: 5, name: '监控发现' },
        { checkbox: false, id: 6, name: '12345市长热线' }
      ],
      dialogFormVisible: false,
      // // 弹框标题
      dialogStatus: '',
      textMap: {
        update: '修改问题来源',
        create: '新增问题来源'
      },
      // 弹框数据
      temp: {
        id: null,
        name: ''
      },
      selectindex: 0
    }
  },
  methods: {
    // 新增流程
    addquestion() {
      this.dialogFormVisible = true
      this.dialogStatus = 'create'
    },
    // 删除
    deletequestion() {
      this.$confirm('确认删除选中的问题来源？', '提示', {
        distinguishCancelAndClose: true,
        confirmButtonText: '确认',
        cancelButtonText: '取消'
      })
        .then(() => {
          const newDate = this.tableData.filter(item => {
            if (!item.checkbox) {
              return item
            }
          })
          console.log(newDate)
          this.tableData = newDate
          this.$message({
            type: 'info',
            message: '已删除'
          })
        })
        .catch(action => {
          this.$message({
            type: 'info',
            message: '已取消删除'
          })
        })
    },
    updatequestion(data, index) {
      this.dialogFormVisible = true
      this.dialogStatus = 'update'
      this.selectindex = index
      this.temp = {
        id: data.id,
        name: data.name
      }
    },
    cancel() {
      this.dialogFormVisible = false
      this.temp = {
        id: null,
        name: ''
      }
    },
    onSubmit() {
      const newDate = {
        checkbox: true,
        id: this.temp.id,
        name: this.temp.name
      }
      if (this.dialogStatus === 'create') {
        this.tableData.unshift(newDate)
      } else {
        this.tableData.splice(this.selectindex, 1, newDate)
      }

      this.dialogFormVisible = false
      this.temp = {
        id: null,
        name: ''
      }
    }
  }
}
